<?php	require_once('config.php');	require_once('proc.php');	require_once('classes/mysql.php');	require_once('classes/synchronizable.php');		class LangVar extends Synchronizable	{				public static $table = 'ums_langvars';				protected $id;		protected $name;		protected $language;		protected $value;				public function getID() { return $this->id; } // Synchronizable		protected function getDataProperties() { return array('name', 'language', 'value'); } // Synchronizable		protected function getDataArrayProperty() { null; } // Synchronizable				public function getName() { return $this->name; }		public function getLanguage() { return $this->language; }		public function getValue() { return $this->value; }				protected function setID($value) { $this->id = $value; } // Synchronizable				public function setName($value) { $this->name = $value; }		public function setLanguage($value) { $this->language = $value; }		public function setValue($value) { $this->value = $value; }				public function __construct()		{			$this->id = -1;		}				public static function get($name)		{			global $config;						$values = Synchronizable::load('LangVar', 'ums_langvars', array('name' => $name, 'language' => $config['language']));			if (is_null($values))				return '[var]' . $name;			else				return $values[0]->getValue();		}			}?>