<?php	require_once('classes/tableeditor.php');	require_once('classes/user.php');	require_once('classes/usergroupassociation.php');	require_once('classes/listeners/tableeditorlistener.php');		class DeleteListener implements TableEditorListener	{		// not required		public function onDisplayList() {}		public function onEdit($id) {}		public function onCreate() {}		public function onSaved($id) {}		public function onSwapped($id1, $id2) {}				// required		public function onDeleted($id)		{			MySQL::executeQuery('DELETE FROM `' . UserGroupAssociation::$table . '` WHERE `user`=\'' . $id . '\'');		}	}		$edit = new TableEditor(User::$table);	$edit->addListener(new DeleteListener());	$edit->show();		if (!isset($_GET['tMode']) || $_GET['tMode'] == 'list')	{?><hr /><p><a href="?page=users&tMode=create">Benutzer manuell erstellen</a></p><?php	}?>