<?php	require_once('classes/synchronizable.php');		class UserGroup extends Synchronizable	{			/*		abstract protected function getID();		abstract protected function getDataProperties();		abstract protected function getDataArrayProperty();				abstract protected function setID($value);*/				public static $table = 'ums_usergroups';				private $id;		protected $name;		protected $superGroup;				public function getID() { return $this->id; }		protected function getDataProperties() { return array('name', 'superGroup'); }		protected function getDataArrayProperty() { return null; }				public function getName() { return $this->name; }				public function getSuperGroup()		{			if (is_null($this->superGroup))				return null;			else			{				$groups = Synchronizable::load('UserGroup', UserGroup::$table, array('id' => $this->superGroup));				if ($groups == null)					return null;				else					return $groups[0];			}		}				public function getSuperGroupID() { return $this->superGroup; }				protected function setID($value) { $this->id = $value; }				public function setName($value) { $this->name = $value; }		public function setSuperGroupID($value) { $this->superGroup = $value; }				public function __construct()		{			$this->id = -1;		}		}?>